import XCTest

final class LognotTests: XCTestCase {
    
    // brun -c '(lognot (q . 12))'
    // cost = 365
    // -13
    func test_lognot_1() throws {
        try verify_program(
            program: "ff1bffff010c80",
            expected_output: "81f3",
            expected_cost: 365
        )
    }
    
    // brun -c '(lognot (q . -1))'
    // cost = 355
    // ()
    func test_lognot_2() throws {
        try verify_program(
            program: "ff1bffff0181ff80",
            expected_output: "80",
            expected_cost: 355
        )
    }
    
    // brun -c '(lognot (q . 0))'
    // cost = 362
    // -1
    func test_lognot_3() throws {
        try verify_program(
            program: "ff1bffff018080",
            expected_output: "81ff",
            expected_cost: 362
        )
    }
    
    // brun -c '(lognot (q . 734671943749191))'
    // cost = 443
    // 0xfd63d1dbc431b8
    func test_lognot_4() throws {
        try verify_program(
            program: "ff1bffff0187029c2e243bce4780",
            expected_output: "87fd63d1dbc431b8",
            expected_cost: 443
        )
    }
    
    // brun -c '(lognot)'
    // FAIL: lognot takes exactly 1 argument ()
    func test_lognot_5() throws {
        try verify_throwing_program(
            program: "ff1b80",
            expected_output: "lognot takes exactly 1 argument"
        )
    }
    
    // brun -c '(lognot (q . (foo)))'
    // FAIL: lognot requires int args ("foo")
    func test_lognot_6() throws {
        try verify_throwing_program(
            program: "ff1bffff01ff83666f6f8080",
            expected_output: "lognot requires int args"
        )
    }
    
    // brun -n -c '(lognot (q . 1) (q . 2))'
    // FAIL: lognot takes exactly 1 argument (1 2)
    func test_lognot_7() throws {
        try verify_throwing_program(
            program: "ff1bffff0101ffff010280",
            expected_output: "lognot takes exactly 1 argument"
        )
    }
    
    // brun -c '(lognot (q . ))'
    // cost = 14894
    // -2
    func test_lognot_8() throws {
        try verify_program(
            program: "",
            expected_output: "81fe",
            expected_cost: 14894
        )
    }
    
}
